drop database if exists rh;
create database rh;
use rh;
create table usuario(
nome varchar(255) ,
email varchar(255) primary key,
senha varchar(255)
);
create table adm(
nome varchar(255) primary key,
senha varchar(255)
);
create table noticia(
id_noticia int auto_increment not null primary key,
img varchar(255),
img2 varchar(255),
card_body varchar(500),
not_tl varchar(300),
not_sub varchar(450),
not_b varchar(10000),
not_font varchar(500),
not_date varchar(255)
);
create table moderador(
    id_mod int auto_increment not null primary key,
    nome varchar(255),
    senha varchar(255)
);

